select

from inbound_plan_detail
where 1=1

  and plan_id = #{pojo.planId,jdbcType=INTEGER}


  and purchase_id = #{pojo.purchaseId,jdbcType=INTEGER}


  and purchase_code like concat('%',#{pojo.purchaseCode,jdbcType=VARCHAR},'%')


  and purchase_row = #{pojo.purchaseRow,jdbcType=INTEGER}


  and commodity_id = #{pojo.commodityId,jdbcType=INTEGER}


  and commodity_name like concat('%',#{pojo.commodityName,jdbcType=VARCHAR},'%')


  and commodity_code like concat('%',#{pojo.commodityCode,jdbcType=VARCHAR},'%')


  and sku_id = #{pojo.skuId,jdbcType=INTEGER}


  and SKU_code like concat('%',#{pojo.skuCode,jdbcType=VARCHAR},'%')


  and SKU_name like concat('%',#{pojo.skuName,jdbcType=VARCHAR},'%')


  and predict_receive_location = #{pojo.predictReceiveLocation,jdbcType=INTEGER}


  and predict_receive_location_code = #{pojo.predictReceiveLocationCode,jdbcType=VARCHAR}


  and total_weight = #{pojo.totalWeight,jdbcType=DOUBLE}


  and total_net_weight = #{pojo.totalNetWeight,jdbcType=DOUBLE}


  and total_volumn = #{pojo.totalVolumn,jdbcType=DOUBLE}


  and unit = #{pojo.unit,jdbcType=VARCHAR}


  and is_checked = #{pojo.isChecked,jdbcType=TINYINT}


  and receive_time = #{pojo.receiveTime,jdbcType=TIMESTAMP}


  and remark = #{pojo.remark,jdbcType=VARCHAR}


  and is_plate = #{pojo.isPlate,jdbcType=TINYINT}


  and is_received = #{pojo.isReceived,jdbcType=TINYINT}


  and is_packaged = #{pojo.isPackaged,jdbcType=BIT}


  and product_company like concat('%',#{pojo.productCompany,jdbcType=VARCHAR},'%')


  and product_time = #{pojo.productTime,jdbcType=TIMESTAMP},


  and product_batch like concat('%',#{pojo.productBatch,jdbcType=VARCHAR},'%')


  and container_id = #{pojo.containerId,jdbcType=INTEGER}


  and container_code like concat('%',#{pojo.containerCode,jdbcType=VARCHAR},'%')


  and container_name like concat('%',#{pojo.containerName,jdbcType=VARCHAR},'%')


  and container_barcode like concat('%',#{pojo.containerBarcode,jdbcType=VARCHAR},'%')


  and num = #{pojo.num,jdbcType=DOUBLE}


  and received_num = #{pojo.receivedNum,jdbcType=DOUBLE}


  and inbound_trace_code like concat(#{pojo.inboundTraceCode,jdbcType=VARCHAR},'%')


  and inventory_id = #{pojo.inventoryId,jdbcType=INTEGER}


  and stack_id = #{pojo.stackId,jdbcType=INTEGER}


  and stack_code like concat(#{pojo.stackCode,jdbcType=VARCHAR},'%')


  and stack_name like concat(#{pojo.stackName,jdbcType=VARCHAR},'%')


  and stack_barcode like concat(#{pojo.stackBarcode,jdbcType=VARCHAR},'%')


  and custom_id = #{pojo.customId,jdbcType=INTEGER}


  and custom_code like concat(#{pojo.customCode,jdbcType=VARCHAR},'%')


  and custom_name like concat(#{pojo.customName,jdbcType=VARCHAR},'%')


  and supplier_name like concat(#{pojo.supplierName,jdbcType=VARCHAR},'%')


  and supplier_id = #{pojo.supplierId,jdbcType=INTEGER}


  and supplier_code like concat(#{pojo.supplierCode,jdbcType=VARCHAR},'%')


